from ms_srvs.structures.share_enum_struct import ShareEnumStruct
from ms_srvs.structures.share_info_container import ShareInfo1Container


class TestShareEnumStructDeseralization():
    share_enum_struct, num_bytes = ShareEnumStruct.from_bytes(
        data=bytes.fromhex('')
    )

    def test_num_bytes(self):
        assert self.num_bytes == 364

    def test_level(self):
        assert self.share_enum_struct.level == 1

    def test_share_info_type(self):
        assert isinstance(self.share_enum_struct.share_info, ShareInfo1Container)
